mapper.select:
select 

from exception
where AppSheetSerialNo = #{appsheetserialno,jdbcType=VARCHAR}
  and TACode = #{tacode,jdbcType=VARCHAR}
  and DistributorCode = #{distributorcode,jdbcType=VARCHAR}
  and ReferenceNo = #{referenceno,jdbcType=INTEGER}

mapper.delete:
delete from exception
where AppSheetSerialNo = #{appsheetserialno,jdbcType=VARCHAR}
  and TACode = #{tacode,jdbcType=VARCHAR}
  and DistributorCode = #{distributorcode,jdbcType=VARCHAR}
  and ReferenceNo = #{referenceno,jdbcType=INTEGER}

mapper.insert:
insert into exception (AppSheetSerialNo,TACode, DistributorCode, ReferenceNo,
  AppTransactionDate, TransactionAccountID, 
  BusinessCode, ErrorType, Speification
  )
values (#{appsheetserialno,jdbcType=VARCHAR},#{tacode,jdbcType=VARCHAR}, #{distributorcode,jdbcType=VARCHAR}, #{referenceno,jdbcType=INTEGER},
  #{apptransactiondate,jdbcType=CHAR}, #{transactionaccountid,jdbcType=VARCHAR}, 
  #{businesscode,jdbcType=CHAR}, #{errortype,jdbcType=OTHER}, #{speification,jdbcType=VARCHAR}
  )

mapper.update:
update exception


    AppTransactionDate = #{apptransactiondate,jdbcType=CHAR},


    TransactionAccountID = #{transactionaccountid,jdbcType=VARCHAR},


    BusinessCode = #{businesscode,jdbcType=CHAR},


    ErrorType = #{errortype,jdbcType=OTHER},


    Speification = #{speification,jdbcType=VARCHAR},


where AppSheetSerialNo = #{appsheetserialno,jdbcType=VARCHAR}
  and TACode = #{tacode,jdbcType=VARCHAR}
  and DistributorCode = #{distributorcode,jdbcType=VARCHAR}
  and ReferenceNo = #{referenceno,jdbcType=INTEGER}

update exception
set AppTransactionDate = #{apptransactiondate,jdbcType=CHAR},
  TransactionAccountID = #{transactionaccountid,jdbcType=VARCHAR},
  BusinessCode = #{businesscode,jdbcType=CHAR},
  ErrorType = #{errortype,jdbcType=OTHER},
  Speification = #{speification,jdbcType=VARCHAR}
where AppSheetSerialNo = #{appsheetserialno,jdbcType=VARCHAR}
  and TACode = #{tacode,jdbcType=VARCHAR}
  and DistributorCode = #{distributorcode,jdbcType=VARCHAR}
  and ReferenceNo = #{referenceno,jdbcType=INTEGER}
